labNumber = document.querySelectorAll('#labNumber');
labTag = document.querySelectorAll('#labTag');

for (i = 0; i < labNumber.length; i++) {
  switch (labNumber[i].textContent) {
    case "Lab 2":
      labTag[i].style.backgroundColor = "#3498db";
      break;

    case "Lab 3":
      labTag[i].style.backgroundColor = "#16a085";
      break;

    case "Lab 4":
      labTag[i].style.backgroundColor = "#f39c12";
      break;
  }
}

addSchedulingButton = document.querySelector('#addScheduling');
editButton = document.querySelectorAll('#scheduleCardButtonsEdit');

modalTitle = document.querySelector('#schedulingTitle');
modalSubmit = document.querySelector('#schedulingSubmit');

addSchedulingButton.addEventListener("click", function() {
  modalTitle.textContent = 'Nova reserva';
  modalSubmit.setAttribute("value", "Criar reserva");
});

for (i = 0; i < editButton.length; i++) {
  editButton[i].addEventListener("click", function() {
    modalTitle.textContent = 'Editar reserva';
    modalSubmit.setAttribute("value", "Salvar");
  });
}
modalContainer = document.querySelector('#modalSchedulingContainer');
addSchedulingButton = document.querySelector('#addScheduling');
modalDarken = document.querySelector('#modalSchedulingDarken');
editButton = document.querySelectorAll('#scheduleCardButtonsEdit');


addSchedulingButton.addEventListener("click", function() {
  modalTrigger();
});

modalDarken.addEventListener("click", function() {
  modalTrigger();
});

for (i = 0; i < editButton.length; i++) {
  editButton[i].addEventListener("click", function() {
    modalTrigger();
  });
}

function modalTrigger() {
  if (addSchedulingButton.style.transform == "rotate(45deg)") {
    addSchedulingButton.style.transform = "rotate(0)";
  } else {
    addSchedulingButton.style.transform = "rotate(45deg)";
  }

  if (modalContainer.style.display == "flex") {
    modalContainer.style.display = "none";
  } else {
    modalContainer.style.display = "flex";
  }
}